// Blogs.jsx
import React, { useState } from "react";
import BlogPreview from "./../components/BlogsPreview";

const dummyBlogs = [
  {
    id: 1,
    title: "Modeling Christ for the Next Generation",
    content:
      "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Curabitur ac leo nunc. Vestibulum et mauris vel ante finibus maximus.",
    image: "data:image/jpeg;base64,",
    tags: ["faith", "youth"],
    status: "published",
    created_at: "2025-05-14",
    updated_at: "2025-05-14",
  },
  {
    id: 2,
    title: "How to Honor God and Government",
    content:
      "Suspendisse potenti. Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas.",
    image: "data:image/jpeg;base64,",
    tags: ["honor", "governance"],
    status: "published",
    created_at: "2025-05-06",
    updated_at: "2025-05-06",
  },
  {
    id: 3,
    title: "Slow Down to Hear Christ’s Voice: God Still Speaks!",
    content:
      "Ut venenatis tellus in metus vulputate eu scelerisque felis. Maecenas volutpat blandit aliquam etiam erat velit scelerisque.",
    image: "https://media.istockphoto.com/id/516234360/vector/slow-down.jpg?s=612x612&w=0&k=20&c=mIL0kge_Rd_Zi8oW4EV1sbOD3wiLIMvdjAHBG7TaW4U=",
    tags: ["spirituality", "peace"],
    status: "published",
    created_at: "2025-04-30",
    updated_at: "2025-04-30",
  },
];

export default function Blogs() {
  const [selectedBlog, setSelectedBlog] = useState(null);

  return (
    <div className="px-10 py-16">
      <h2 className="text-center text-xl mb-8">
        Our blog takes the message from the weekend and lays out next right
        steps, so you can hear a message and do a message in practical ways.
      </h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {dummyBlogs.map((blog) => (
          <div
            key={blog.id}
            className="shadow-lg rounded-lg overflow-hidden cursor-pointer hover:scale-105 transition duration-300"
            onClick={() => setSelectedBlog(blog)}
          >
            <img src={blog.image} alt={blog.title} className="w-full h-48 object-cover" />
            <div className="p-4">
              <h3 className="font-semibold text-lg mb-2">{blog.title}</h3>
              <p className="text-sm text-gray-500 mb-2">{blog.created_at}</p>
              <button className="text-purple-700 text-sm">VIEW THESE RESOURCES</button>
            </div>
          </div>
        ))}
      </div>

      {selectedBlog && (
        <BlogPreview blog={selectedBlog} onClose={() => setSelectedBlog(null)} />
      )}
    </div>
  );
}
